INSERT INTO `configurations` (`configID`, `sidebarSide`, `primaryColor`, `secondaryColor`, `linkHoverColor`, `bodyBackgroundColor`, `bodyBackgroundImage`, `customCSS`, `sidebarContent`, `sidebarStream`, `streamLink`, `embeddedLink`, `radioName`, `programName`, `albumName`, `locale`, `siteDir`)
VALUES
	(1,1,'#2DA5DA','#2DA5DA','#35C1FF','#F9F9F9','../img/bg.jpg','/*\r\n\r\nEnter custom css here. For example, entering the following will make the background green:\r\n\r\n  body{\r\n    background-color: green;\r\n    background-image: none;\r\n  }\r\n\r\n*/','<p>The information for this sidebar can be edited inside of the Admin Panel under the \"Sidebar\" section. It will be displayed along the side of most pages on the website, so it is a good place to put critical information that visitors need to see throughout the whole website. Pages also have their own unique sidebars that will appear on the opposite side of the page from this sidebar (a good example of this is the \"Contact Us\" page).</p>\r\n<p>Информация для этого боковой панели могут быть отредактированы внутри панели администратора в разделе \"Боковая панель\". Это будет отображаться вдоль стороны большинства страниц на веб-сайте, так что это хорошее место, чтобы положить важную информацию, что посетители должны видеть в течение всего веб-сайта. Страницы также имеют свои собственные уникальные боковые панели, которые будут появляться на противоположной стороне страницы из этой боковой панели (Хорошим примером этого является страница \"Обратная связь\").</p>',1,'https://emissionradio.org:2199/tunein/kyrgyz','http://50.248.216.230:8003/stream','Radio Name','Program Name','Album Name','usa','/development');
